{
  "madres_famosas": [
    {
      "nombre": "marie curie",
      "descripcion": "una madre que no solo crió a su hija irène, sino que también le inculcó el amor por la ciencia al punto de que ambas ganaron premios nobel."
    },
    {
      "nombre": "mary shelley",
      "descripcion": "dio vida a frankenstein y también a su hijo, mientras navegaba en un mundo literario dominado por hombres."
    },
    {
      "nombre": "michelle obama",
      "descripcion": "además de ser primera dama, abogada y escritora, se ha dedicado a inspirar a sus hijas sasha y malia para que sean mujeres independientes y fuertes."
    },
    {
      "nombre": "serena williams",
      "descripcion": "dominó el tenis mundial y luego demostró que la maternidad y el alto rendimiento deportivo pueden ir de la mano."
    },
    {
      "nombre": "cleopatra",
      "descripcion": "la última faraona de egipto no solo gobernó con astucia, sino que también fue madre de cesarión, el hijo de julio césar."
    }
  ]
}
