	for (let i = 0; i < n; i++) {
		const xi = x[i];
		const vx = v[i];
		ctx.fillStyle = "white";
		ctx.fillRect(xi - vx / 2, 0, vx / 2, canvas.height);
		ctx.fillStyle = "black";
